mapper.select:
SELECT * FROM board
subject LIKE CONCAT('%', #{search}, '%')
ORDER BY reRef DESC, reSeq ASC LIMIT #{amount} OFFSET #{startRow}

mapper.update:
UPDATE board 
SET readCount = readCount+1 
WHERE num = #{num}

UPDATE board 
SET subject=#{subject}, content=#{content} 
WHERE num=#{num}

UPDATE board 
SET reSeq = reSeq + 1 
WHERE reRef = #{reRef} 
AND reSeq > #{reSeq}
